      (function() {
        'use strict';


        if (document.readyState === 'loading') {
          document.addEventListener('DOMContentLoaded', function() {
            renderMenu();
          });
        } else {
          renderMenu();
        }


        function renderMenu() {
          var container = document.getElementById('menuSections');
          var loadingContainer = document.getElementById('loadingContainer');
          var errorContainer = document.getElementById('errorContainer');
          var catalog = window.FUSION_CATALOG || [];


          if (loadingContainer) loadingContainer.style.display = 'none';
          if (errorContainer) errorContainer.style.display = 'none';


          if (catalog.length > 0) {
            console.log('STANDALONE SCRIPT: Rendering ' + catalog.length + ' categories...');

            var html = '<div class="d-flex justify-content-between align-items-center mb-4"><h2 class="fw-bold mb-0">Products</h2></div><div class="row g-4">';


            catalog.forEach(function(category) {
              if (category.posts.length > 0) {
                 html += `
                  <div class="category-section" style="margin-bottom: 4rem; scroll-margin-top: 150px;">
                    <div class="category-header">
                      <h3 class="category-title">${category.title}</h3>
                    </div>
                    <div class="menu-grid">
                 `;

                 category.posts.forEach(function(post) {
                   html += `
                           <div class="col-6 col-md-4 col-lg-3">
                             <div class="card h-100 shadow-sm border-0">
                               <img src="${post.imageUrl}" class="card-img-top" alt="${post.title}" style="height: 200px; object-fit: cover;">
                               <div class="card-body d-flex flex-column">
                                 <h5 class="card-title">${post.title}</h5>
                                 <p class="card-text small text-muted">${post.description || ''}</p>
                                 <div class="mt-auto mb-3">
                                   <span class="h5 text-primary fw-bold mb-0">${post.price}</span>
                                 </div>
                                 <div class="d-grid gap-2">
                                   <button class="btn btn-outline-primary btn-sm rounded-pill" onclick="window.FUSION.Wishlist.toggle('${post.id}')">
                                     <i class="bi bi-heart"></i>
                                   </button>
                                   <button class="btn btn-primary btn-sm flex-grow-1" onclick="window.FUSION.Cart.add('${post.id}')">
                                     <i class="bi bi-cart-plus me-1"></i> Add to Cart
                                   </button>
                                 </div>
                               </div>
                             </div>`;
                 });

                 html += `</div></div>`;
              }
            });

            container.innerHTML = html;
          } else {
            // 3. EMPTY STATE
            console.log('STANDALONE SCRIPT: Catalog is empty.');
            container.innerHTML = '<div class="col-12 text-center py-5"><h3 class="text-muted">Menu Initializing...</h3>';


            setTimeout(function() {
              var updatedCatalog = window.FUSION_CATALOG || [];
              if (updatedCatalog.length > 0) {
                renderMenu();
              } else if (updatedCatalog.length === 0) {

                 console.log('STANDALONE SCRIPT: No products found.');
                 container.innerHTML = '<div class="col-12 text-center py-5"><h3 class="text-muted">No products found in catalog.</h3>';
              }
            }, 500);
          })();
        }
      }
    });

