          {id:"p4",cat:"Taco",title:"Salmon Avocado Taco",price:40,img:"https://images.unsplash.com/photo-1617196034183-421b4917c92d?w=600",desc:"Light salmon cubes with lime-infused avocado mash.",inStock:true},
          {id:"p5",cat:"Appetizer",title:"Dynamite Shrimp",price:85,img:"https://images.unsplash.com/photo-1559715745-e1b33a271c8f?w=600",desc:"Tempura battered shrimp in a spicy fire glaze.",inStock:true},
          {id:"p6",cat:"Appetizer",title:"Classic Miso Soup",price:30,img:"https://images.unsplash.com/photo-1547592166-23ac45744acd?w=600",desc:"Traditional broth with silk tofu and spring onions.",inStock:true}
        ],
        gallery:[
          "https://images.unsplash.com/photo-1579584425555-c3ce17fd4351?q=80&w=500",
          "https://images.unsplash.com/photo-1611143669185-af224c5e3252?q=80&w=500",
          "https://images.unsplash.com/photo-1562436260-8c9216eeb703?q=80&w=500",
          "https://images.unsplash.com/photo-1553621042-f6e147245754?q=80&w=500",
          "https://images.unsplash.com/photo-1617196034651-361730097f4a?q=80&w=500",
          "https://images.unsplash.com/photo-1583623025817-d180a2221d0a?q=80&w=500"
        ],
        promos:[
          {code:"SUSHI10",val:.1,type:"percent"},
          {code:"MAD50",val:50,type:"fixed"}
        ],
        state:{
          currency:localStorage.getItem("fe_curr")||"MAD",
          cart:JSON.parse(localStorage.getItem("fe_cart"))||[],
          wish:JSON.parse(localStorage.getItem("fe_wish"))||[],
          user:JSON.parse(localStorage.getItem("fe_user"))||null,
          revs:JSON.parse(localStorage.getItem("fe_revs"))||{"p1":[{u:"Karim",s:5,t:"Fresh!"}],"p2":[]},
          discount:0,
          discountMsg:""
        }
      };

      /* UTILITIES */
      const $=s=>document.querySelector(s),$$=s=>document.querySelectorAll(s);
      const fmt=p=>Math.round(p*FE.rates[FE.state.currency]*100)/100+" "+FE.sym[FE.state.currency];
      const save=()=>{localStorage.setItem("fe_cart",JSON.stringify(FE.state.cart));localStorage.setItem("fe_wish",JSON.stringify(FE.state.wish));localStorage.setItem("fe_curr",FE.state.currency);localStorage.setItem("fe_revs",JSON.stringify(FE.state.revs));localStorage.setItem("fe_user",JSON.stringify(FE.state.user))};

      /* RENDER FUNCTIONS */
      function renderMenu(){
        const g=$("#menuGrid");g.innerHTML="";
        FE.catalog.forEach(p=>{
          const c=FE.state.cart.find(i=>i.id===p.id);
          const w=FE.state.wish.includes(p.id);
          g.insertAdjacentHTML("beforeend",`
            <div class='fe-card ${!p.inStock?"out-of-stock":""}' data-cat='${p.cat}'>
              <div class='card-media-box'>
                <img src='${p.img}' alt='${p.title}'/>
                <button class='wish-overlay ${w?"active":""}' data-id='${p.id}'><i class='bi bi-heart-fill'></i></button>
              </div>
              <div class='card-info-box'>
                <h5 class='fw-bold'>${p.title}</h5>
                <div class='star-display'>★★★★☆</div>
                <p class='small text-muted mb-2'>${p.desc}</p>
                <div class='d-flex justify-content-between align-items-center'>
                  <span class='card-price-tag'>${fmt(p.price)}</span>
                  <button class='btn btn-sm btn-dark rounded-pill add-cart' data-id='${p.id}' ${!p.inStock?"disabled":""}>Add</button>
                </div>
              </div>
            </div>`);
        });
      }
      function renderGallery(){
        const g=$("#galleryGrid");g.innerHTML="";
        FE.gallery.forEach(src=>g.insertAdjacentHTML("beforeend",`<div class='gallery-frame'><img src='${src}' alt='Gallery'/></div>`));
      }
      function renderCart(){
        const l=$("#sideCartItemsList"),f=$("#sideCartFooter"),b=$("#cartBadge"),t=$("#feFinalTotalDisplay");
        b.textContent=FE.state.cart.length;
        l.innerHTML="";
        let total=0;
        FE.state.cart.forEach(i=>{
          const p=FE.catalog.find(c=>c.id===i.id);
          total+=p.price*i.qty;
          l.insertAdjacentHTML("beforeend",`
            <div class='cart-row-item'>
              <img src='${p.img}' alt='${p.title}'/>
              <div class='flex-grow-1'>
                <h6 class='fw-bold mb-1'>${p.title}</h6>
                <div class='qty-engine'>
                  <span class='qty-btn' data-id='${p.id}' data-op='-'>-</span>
                  <span>${i.qty}</span>
                  <span class='qty-btn' data-id='${p.id}' data-op='+'>+</span>
                </div>
              </div>
              <span class='fw-bold'>${fmt(p.price*i.qty)}</span>
            </div>`);
        });
        if(FE.state.discount){
          if(FE.state.discount.type==="percent") total*=1-FE.state.discount.val;
          else total-=FE.state.discount.val;
        }
        t.textContent=fmt(total);
        f.style.display=FE.state.cart.length?"block":"none";
      }
      function renderWish(){
        const l=$("#sideWishItemsList");
        l.innerHTML="";
        FE.state.wish.forEach(id=>{
          const p=FE.catalog.find(c=>c.id===id);
          l.insertAdjacentHTML("beforeend",`
            <div class='cart-row-item'>
              <img src='${p.img}' alt='${p.title}'/>
              <div class='flex-grow-1'>
                <h6 class='fw-bold mb-1'>${p.title}</h6>
                <span class='fw-bold'>${fmt(p.price)}</span>
              </div>
              <button class='btn btn-sm btn-outline-danger rounded-pill remove-wish' data-id='${p.id}'>×</button>
            </div>`);
        });
      }
      function openSidebar(id){$(id).classList.add("open");$("#sideBackdrop").style.display="block"}
      function closeSidebar(){["#sideCartPanel","#sideWishPanel"].forEach(s=>$(s).classList.remove("open"));$("#sideBackdrop").style.display="none"}
      function openModal(id){$(id).style.display="flex"}
      function closeModal(){["#modalQuickView","#modalAdminPanel","#modalTableBooking","#modalAuth"].forEach(m=>$(m).style.display="none")}
      function scrollReveal(){
        $$(".reveal").forEach(el=>{
          const rect=el.getBoundingClientRect();
          if(rect.top<window.innerHeight-100) el.classList.add("active");
        });
      }

      /* EVENT BINDINGS */
      document.addEventListener("click",e=>{
        if(e.target.closest(".add-cart")){
          const id=e.target.closest(".add-cart").dataset.id;
          const item=FE.state.cart.find(i=>i.id===id);
          item?item.qty++:FE.state.cart.push({id,qty:1});
          save();renderCart();
        }
        if(e.target.closest(".wish-overlay")){
          const id=e.target.closest(".wish-overlay").dataset.id;
          const idx=FE.state.wish.indexOf(id);
          idx>-1?FE.state.wish.splice(idx,1):FE.state.wish.push(id);
          e.target.closest(".wish-overlay").classList.toggle("active");
          save();renderWish();
        }
        if(e.target.closest(".remove-wish")){
          const id=e.target.closest(".remove-wish").dataset.id;
          FE.state.wish.splice(FE.state.wish.indexOf(id),1);
          save();renderWish();renderMenu();
        }
        if(e.target.closest(".qty-btn")){
          const{id,op}=e.target.closest(".qty-btn").dataset;
          const item=FE.state.cart.find(i=>i.id===id);
          if(op==="+") item.qty++; else item.qty--;
          if(item.qty<=0) FE.state.cart.splice(FE.state.cart.indexOf(item),1);
          save();renderCart();
        }
      });
      $("#currSelect").addEventListener("change",e=>{FE.state.currency=e.target.value;save();renderMenu();renderCart()});
      $("#btnApplyPromo").addEventListener("click",()=>{
        const v=$("#fePromoIn").value.trim();
        const p=FE.promos.find(c=>c.code===v);
        if(p){FE.state.discount=p;$("#fePromoStatus").textContent="Promo applied!";}else{$("#fePromoStatus").textContent="Invalid code";}
        renderCart();
      });
      $("#btnFinalCheckoutWA").addEventListener("click",()=>{
        const msg=FE.state.cart.map(i=>{const p=FE.catalog.find(c=>c.id===i.id);return`${i.qty}x ${p.title} (${fmt(p.price*i.qty)})`}).join(", ");
        window.open(`https://wa.me/${FE.waLink}?text=Order: ${msg}`, "_blank");
      });
      $("#btnTriggerContactWA").addEventListener("click",()=>window.open(`https://wa.me/${FE.waLink}?text=Hello%20Event%20Sushi`, "_blank"));
      $("#btnOrderNow").addEventListener("click",()=>openSidebar("#sideCartPanel"));
      $("#btnBookTable").addEventListener("click",()=>openModal("#modalTableBooking"));
      $("#btnSubmitBooking").addEventListener("click",()=>{
        const n=$("#bookInName").value,p=$("#bookInPhone").value,d=$("#bookInDate").value,t=$("#bookInTime").value,g=$("#bookInGuests").value;
        if(!n||!p||!d||!t) return alert("Fill all fields");
        window.open(`https://wa.me/${FE.waLink}?text=Reservation: ${n}, ${p}, ${d} at ${t} for ${g} guests`,"_blank");
        closeModal();
      });
      $("#btnActionLogin").addEventListener("click",()=>{
        const e=$("#loginMailIn").value;
        if(e==="admin@eventsushi.com"){FE.state.user={admin:true};$("#adminFlag").style.display="inline-block";openModal("#modalAdminPanel");}else{FE.state.user=null;$("#adminFlag").style.display="none";}
        save();closeModal();
      });
      $("#adminFlag").addEventListener("click",()=>openModal("#modalAdminPanel"));
      $("#cartToggle").addEventListener("click",()=>openSidebar("#sideCartPanel"));
      $("#wishToggle").addEventListener("click",()=>openSidebar("#sideWishPanel"));
      $("#feFloatCartBtn").addEventListener("click",()=>openSidebar("#sideCartPanel"));
      $("#closeSideCart").addEventListener("click",closeSidebar);
      $("#closeSideWish").addEventListener("click",closeSidebar);
      $("#closeModalQV").addEventListener("click",closeModal);
      $("#closeModalAdmin").addEventListener("click",closeModal);
      $("#closeModalBook").addEventListener("click",closeModal);
      $("#closeModalAuth").addEventListener("click",closeModal);
      $("#sideBackdrop").addEventListener("click",closeSidebar);

      /* INIT */
      renderMenu();renderGallery();renderCart();renderWish();scrollReveal();
      window.addEventListener("scroll",scrollReveal);
    })();
