body.script:
//
document.addEventListener("DOMContentLoaded", function() {
  (function(){
    const FE = {
      config: { waPhone: "212600000000", storeName: "EVENT SUSHI" },
      catalog: [
        { id: "p1", title: "Salmon Lovers Box", price: 120, img: "https://images.unsplash.com/photo-1579871494447-9811cf80d66c?w=600", desc: "12 pieces of premium fresh salmon selection." },
        { id: "p2", title: "Dragon Platter", price: 155, img: "https://images.unsplash.com/photo-1553621042-f6e147245754?w=600", desc: "Tempura shrimp roll topped with grilled unagi." },
        { id: "p3", title: "Spicy Tuna Taco", price: 45, img: "https://images.unsplash.com/photo-1593560708920-61dd98c46a4e?w=600", desc: "Crunchy nori shell filled with spicy marinated tuna." }
      ],
      state: { cart: JSON.parse(localStorage.getItem("fe_cart") || "[]"), wish: JSON.parse(localStorage.getItem("fe_wish") || "[]"), qvId: null, qvQty: 1 },
      $: id => document.getElementById(id),
      $$: s => document.querySelectorAll(s),
      save: function() { localStorage.setItem("fe_cart",JSON.stringify(this.state.cart)); localStorage.setItem("fe_wish",JSON.stringify(this.state.wish)); },

      closeAll: function() {
        this.$$(".fe-sidebar").forEach(s => s.classList.remove("open"));
        this.$$(".fe-modal-backdrop, .fe-search-overlay").forEach(m => m.style.display = "none");
        this.$("feSideBackdrop").style.display = "none";
      },

      renderMenu: function() {
        this.$("feMenuGrid").innerHTML = this.catalog.map(p => `
          <div class="fe-card reveal js-card" data-id="${p.id}">
            <div class="card-img-box"><img src="${p.img}"/><div class="card-actions">
                <button class="grid-btn grid-btn-cart js-quick-add" data-id="${p.id}">Add</button>
                <button class="grid-btn grid-btn-wish js-wish-toggle ${this.state.wish.includes(p.id)?'active':''}" data-id="${p.id}"><i class="bi bi-heart-fill">&#160;</i></button>
            </div></div>
            <div class="card-body"><h6 class="fw-bold">${p.title}</h6><div>${p.price} DH</div></div>
          </div>`).join('');
        const obs = new IntersectionObserver(es => es.forEach(e => { if(e.isIntersecting) e.target.classList.add("active"); }), {threshold: 0.1});
        this.$$(".reveal").forEach(el => obs.observe(el));
      },

      renderCart: function() {
        const valid = this.state.cart.filter(i => this.catalog.find(p => p.id === i.id));
        this.$("feCartList").innerHTML = valid.map(i => {
          let p = this.catalog.find(x => x.id === i.id);
          return `<div class="row-item"><img src="${p.img}"/><div><h6>${p.title}</h6><small>${p.price} DH x ${i.qty}</small></div><div class="ms-auto js-del-cart" data-id="${i.id}">DEL</div></div>`;
        }).join('');
        this.$("feFinalTotalDisplay").textContent = valid.reduce((s,i) => s + (this.catalog.find(x => x.id === i.id).price * i.qty), 0) + " DH";
        this.$("feCartFooter").style.display = valid.length ? "block" : "none";
        this.$("cartBadge").textContent = valid.reduce((s,i) => s + i.qty, 0);
        this.save();
      },

      renderWish: function() {
        const valid = this.state.wish.filter(id => this.catalog.find(p => p.id === id));
        this.$("feWishList").innerHTML = valid.map(id => {
          let p = this.catalog.find(x => x.id === id);
          return `<div class="row-item"><img src="${p.img}"/><div><h6>${p.title}</h6><button class="btn btn-sm btn-dark js-quick-add" data-id="${p.id}">+ Basket</button></div><div class="ms-auto js-del-wish" data-id="${id}">X</div></div>`;
        }).join('');
        this.$("wishBadge").textContent = valid.length;
        this.save();
      },

      bindEvents: function() {
        const self = this;
        document.addEventListener("click", e => {
          const t = e.target;

          if (t.closest("#cartToggle")) { self.$("sideCartPanel").classList.add("open"); self.$("feSideBackdrop").style.display = "block"; return; }
          if (t.closest("#wishToggle")) { self.$("sideWishPanel").classList.add("open"); self.$("feSideBackdrop").style.display = "block"; return; }
          if (t.closest("#btnOpenSearch")) { self.$("searchOverlay").style.display = "flex"; self.$("feSearchIn").focus(); return; }
          if (t.closest(".js-close-all") || t.id === "feSideBackdrop" || t.id === "modalQuickView") { self.closeAll(); return; }

          if (t.closest(".js-del-cart")) { self.state.cart = self.state.cart.filter(x => x.id !== t.closest(".js-del-cart").dataset.id); self.renderCart(); return; }
          if (t.closest(".js-del-wish")) { self.state.wish = self.state.wish.filter(x => x !== t.closest(".js-del-wish").dataset.id); self.renderWish(); self.renderMenu(); return; }

          const card = t.closest(".js-card") || t.closest(".search-item");
          if (card && !t.closest(".card-actions")) {
            const p = self.catalog.find(x => x.id === card.dataset.id);
            self.state.qvId = p.id; self.state.qvQty = 1;
            self.$("feQVImg").src = p.img; self.$("feQVTitle").textContent = p.title; self.$("feQVPrice").textContent = p.price + " DH"; self.$("feQVDesc").textContent = p.desc; self.$("feQVQtyVal").textContent = "1";
            self.$("btnModalWishlist").classList.toggle("active", self.state.wish.includes(p.id));
            self.$("searchOverlay").style.display = "none";
            self.$("modalQuickView").style.display = "flex";
            return;
          }

          if (t.closest(".js-modal-qty")) {
            const op = t.closest(".js-modal-qty").dataset.op;
            op === "+" ? self.state.qvQty++ : (self.state.qvQty > 1 ? self.state.qvQty-- : null);
            self.$("feQVQtyVal").textContent = self.state.qvQty;
            return;
          }

          if (t.id === "btnModalAddToCart" || t.closest(".js-quick-add")) {
            const btn = t.closest(".js-quick-add");
            const id = btn ? btn.dataset.id : self.state.qvId;
            const qty = btn ? 1 : self.state.qvQty;
            const ex = self.state.cart.find(x => x.id === id);
            ex ? ex.qty += qty : self.state.cart.push({id, qty});
            self.closeAll(); self.renderCart(); self.$("sideCartPanel").classList.add("open"); self.$("feSideBackdrop").style.display = "block";
            return;
          }

          if (t.closest(".js-wish-toggle")) {
            const id = t.closest(".js-wish-toggle").dataset.id || self.state.qvId;
            const idx = self.state.wish.indexOf(id);
            idx > -1 ? self.state.wish.splice(idx, 1) : self.state.wish.push(id);
            self.renderWish(); self.renderMenu();
            if(self.$("btnModalWishlist")) self.$("btnModalWishlist").classList.toggle("active", self.state.wish.includes(self.state.qvId));
            return;
          }

          if (t.id === "btnOrderWA") {
              let msg = `ðŸ£ ORDER: ${self.config.storeName}\n------------------\n`;
              self.state.cart.forEach(item => {
                  let p = self.catalog.find(x => x.id === item.id);
                  msg += `â€¢ ${item.qty}x ${p.title} (${p.price * item.qty} DH)\n`;
              });
              window.open(`https://wa.me/${self.config.waPhone}?text=${encodeURIComponent(msg)}`);
          }
        }, true);

        this.$("feSearchIn").onkeyup = e => {
          const v = e.target.value.toLowerCase().trim();
          if(v.length < 2) { self.$("feSearchResults").innerHTML = ""; return; }
          self.$("feSearchResults").innerHTML = self.catalog.filter(p => p.title.toLowerCase().includes(v)).map(p => `<div class="search-item js-card" data-id="${p.id}"><img src="${p.img}"/><div><h6>${p.title}</h6><small>${p.price} DH</small></div></div>`).join('');
        };
      },
      init: function() { this.renderMenu(); this.renderCart(); this.renderWish(); this.bindEvents(); }
    };
    FE.init();
  })();
});
//
