body.script:
//
document.addEventListener("DOMContentLoaded", function() {
  (function(){
    const FE = {
      config: { waPhone: "212600000000", storeName: "EVENT SUSHI" },
      catalog: [
        { id: "p1", title: "Salmon Lovers Box", price: 120, oldPrice: 155, label: "Sale", cat: "Special Rolls", 
          imgs: ["https://images.unsplash.com/photo-1579871494447-9811cf80d66c?w=600", "https://images.unsplash.com/photo-1553621042-f6e147245754?w=600", "https://images.unsplash.com/photo-1593560708920-61dd98c46a4e?w=600"], 
          desc: "Premium selection of 12 pieces including Salmon Nigiri and rolls.", expiry: new Date().getTime() + 7200000 },
        { id: "p2", title: "Dragon Platter", price: 155, label: "Hot", cat: "Special Rolls", 
          imgs: ["https://images.unsplash.com/photo-1553621042-f6e147245754?w=600", "https://images.unsplash.com/photo-1593560708920-61dd98c46a4e?w=600"], 
          desc: "Signature tempura shrimp roll topped with grilled unagi." },
        { id: "p3", title: "Spicy Tuna Nigiri", price: 45, oldPrice: 60, cat: "Nigiri", label: "Sale",
          imgs: ["https://images.unsplash.com/photo-1593560708920-61dd98c46a4e?w=600"], 
          desc: "Hand-pressed sushi rice topped with spicy tuna.", expiry: new Date().getTime() + 3600000 }
      ],
      state: { filter: "all", cart: JSON.parse(localStorage.getItem("fe_cart") || "[]"), wish: JSON.parse(localStorage.getItem("fe_wish") || "[]"), qvId: null, qvQty: 1, qvImgIdx: 0 },
      $: id => document.getElementById(id),
      $$: s => document.querySelectorAll(s),
      save: function() { localStorage.setItem("fe_cart",JSON.stringify(this.state.cart)); localStorage.setItem("fe_wish",JSON.stringify(this.state.wish)); },

      openSidebar: function(id) { this.$(id).classList.add("open"); const b = this.$("feSideBackdrop"); b.style.display="block"; setTimeout(()=>b.style.opacity="1",10); },
      closeAll: function() { this.$$(".fe-sidebar").forEach(s=>s.classList.remove("open")); this.$$(".fe-modal-backdrop").forEach(m=>m.style.display="none"); const b = this.$("feSideBackdrop"); b.style.opacity="0"; setTimeout(()=>b.style.display="none",300); },

      renderMenu: function() {
        const items = this.state.filter === "all" ? this.catalog : this.catalog.filter(p => p.cat === this.state.filter);
        this.$("feMenuGrid").innerHTML = items.map(p => `
          <div class="fe-card reveal js-card" data-id="${p.id}">
            ${p.label ? `<span class="product-label label-${p.label.toLowerCase()}">${p.label}</span>` : ''}
            ${p.expiry ? `<div class="flash-timer js-timer" data-end="${p.expiry}"><span class="timer-val">--:--</span></div>` : ''}
            <div class="card-img-box"><img src="${p.imgs[0]}"/><div class="card-actions">
                <button class="grid-btn grid-btn-cart js-quick-add" data-id="${p.id}"><i class="bi bi-bag-plus">&#160;</i> Add</button>
                <button class="grid-btn grid-btn-wish js-wish-toggle ${this.state.wish.includes(p.id)?'active':''}" data-id="${p.id}"><i class="bi bi-heart-fill">&#160;</i></button>
            </div></div>
            <div class="card-body"><h6 class="fw-bold m-0">${p.title}</h6><div class="mt-2">${p.oldPrice?`<small class='text-decoration-line-through text-muted me-2'>${p.oldPrice} DH</small>`:''}<b>${p.price} DH</b></div></div>
          </div>`).join('');
        this.initTimers();
        const obs=new IntersectionObserver(es=>es.forEach(e=>{if(e.isIntersecting) e.target.classList.add("active");}),{threshold:0.1});
        this.$$(".reveal").forEach(el=>obs.observe(el));
      },

      initTimers: function() {
          const update = () => {
              this.$$(".js-timer").forEach(t => {
                  const diff = parseInt(t.dataset.end) - new Date().getTime();
                  if(diff <= 0) { t.innerHTML = "EXPIRED"; } else {
                      const m = Math.floor((diff % 3600000) / 60000), s = Math.floor((diff % 60000) / 1000);
                      t.querySelector(".timer-val").textContent = `${m}m ${s}s left`;
                  }
              });
          };
          setInterval(update, 1000);
      },

      renderCart: function() {
        const valid = this.state.cart.filter(i=>this.catalog.find(p=>p.id===i.id));
        this.$("feCartList").innerHTML = valid.map(i => {
          let p=this.catalog.find(x=>x.id===i.id);
          return `<div class="row-item"><img src="${p.imgs[0]}"/><div><h6>${p.title}</h6><small>${p.price} DH x ${i.qty}</small></div><div class="ms-auto js-del-cart" data-id="${i.id}" style="cursor:pointer; color:red; font-weight:900">DEL</div></div>`;
        }).join('');
        this.$("feFinalTotalDisplay").textContent = valid.reduce((s,i)=>s+(this.catalog.find(x=>x.id===i.id).price*i.qty),0) + " DH";
        this.$$(".js-cart-meta").forEach(el=>el.style.display = valid.length ? "block" : "none");
        this.$("cartBadge").textContent = valid.reduce((s,i)=>s+i.qty,0);
        this.save();
      },

      renderWish: function() {
        const valid = this.state.wish.filter(id=>this.catalog.find(p=>p.id===id));
        this.$("feWishList").innerHTML = valid.map(id => {
          let p=this.catalog.find(x=>x.id===id);
          return `<div class="row-item"><img src="${p.imgs[0]}"/><div><h6>${p.title}</h6><button class="btn btn-sm btn-dark js-quick-add" data-id="${p.id}">+ Basket</button></div><div class="ms-auto js-del-wish" data-id="${id}" style="cursor:pointer; font-weight:900">X</div></div>`;
        }).join('');
        this.$("wishBadge").textContent = valid.length;
        this.save();
      },

      updateQVImage: function(idx) {
          const p = this.catalog.find(x => x.id === this.state.qvId);
          if(!p || !p.imgs[idx]) return;
          this.state.qvImgIdx = idx;
          this.$("feQVImg").src = p.imgs[idx];
          this.$$(".qv-thumb").forEach((th, i) => th.classList.toggle("active", i === idx));
      },

      bindEvents: function() {
        const self = this;
        document.addEventListener("click", e => {
          const t = e.target;
          if (t.closest("#cartToggle")) { self.openSidebar("sideCartPanel"); return; }
          if (t.closest("#wishToggle")) { self.openSidebar("sideWishPanel"); return; }
          if (t.closest(".cat-item")) {
              self.$$(".cat-item").forEach(c => c.classList.remove("active"));
              t.closest(".cat-item").classList.add("active");
              self.state.filter = t.closest(".cat-item").dataset.cat;
              self.renderMenu(); return;
          }
          if (t.closest(".js-close-all") || t.id === "feSideBackdrop" || t.id === "modalQuickView") { self.closeAll(); return; }

          // STEP 14 GALLERY ACTIONS
          if (t.closest(".js-qv-nav")) {
              const dir = parseInt(t.closest(".js-qv-nav").dataset.dir);
              const p = self.catalog.find(x => x.id === self.state.qvId);
              let nextIdx = self.state.qvImgIdx + dir;
              if(nextIdx < 0) nextIdx = p.imgs.length - 1;
              if(nextIdx >= p.imgs.length) nextIdx = 0;
              self.updateQVImage(nextIdx);
              return;
          }
          if (t.closest(".js-thumb")) {
              self.updateQVImage(Array.from(self.$$(".qv-thumb")).indexOf(t.closest(".js-thumb")));
              return;
          }

          const card = t.closest(".js-card");
          if (card && !t.closest(".card-actions")) {
            const p = self.catalog.find(x=>x.id===card.dataset.id);
            self.state.qvId=p.id; self.state.qvQty=1; self.state.qvImgIdx=0;
            self.$("feQVImg").src=p.imgs[0]; self.$("feQVTitle").textContent=p.title; self.$("feQVPrice").textContent=p.price + " DH";
            self.$("feQVOldPrice").textContent = p.oldPrice ? p.oldPrice + " DH" : "";
            self.$("feQVDesc").textContent=p.desc; self.$("feQVQtyVal").textContent="1";
            self.$("feQVThumbGrid").innerHTML = p.imgs.length > 1 ? p.imgs.map((src, i) => `<img class="qv-thumb js-thumb ${i===0?'active':''}" src="${src}"/>`).join('') : '';
            self.$("btnModalWishlist").classList.toggle("active", self.state.wish.includes(p.id));
            self.$("modalQuickView").style.display="flex";
            return;
          }

          if (t.closest(".js-modal-qty")) {
            const op = t.closest(".js-modal-qty").dataset.op;
            op === "+" ? self.state.qvQty++ : (self.state.qvQty>1 ? self.state.qvQty-- : null);
            self.$("feQVQtyVal").textContent = self.state.qvQty;
            return;
          }

          if (t.id==="btnModalAddToCart" || t.closest(".js-quick-add")) {
            const btn = t.closest(".js-quick-add");
            const id = btn ? btn.dataset.id : self.state.qvId;
            const qty = btn ? 1 : self.state.qvQty;
            const ex=self.state.cart.find(x=>x.id===id);
            ex ? ex.qty+=qty : self.state.cart.push({id, qty});
            self.closeAll(); self.renderCart(); self.openSidebar("sideCartPanel");
            return;
          }

          if (t.closest(".js-wish-toggle")) {
            const id = t.closest(".js-wish-toggle").dataset.id || self.state.qvId;
            const idx = self.state.wish.indexOf(id);
            idx > -1 ? self.state.wish.splice(idx,1) : self.state.wish.push(id);
            self.renderWish(); self.renderMenu();
            if(self.$("btnModalWishlist")) self.$("btnModalWishlist").classList.toggle("active", self.state.wish.includes(self.state.qvId));
            return;
          }

          if (t.closest(".js-del-cart")) { self.state.cart = self.state.cart.filter(x => x.id !== t.closest(".js-del-cart").dataset.id); self.renderCart(); return; }
          if (t.closest(".js-del-wish")) { self.state.wish = self.state.wish.filter(x => x !== t.closest(".js-del-wish").dataset.id); self.renderWish(); self.renderMenu(); return; }

          if (t.id === "btnOrderWA") {
              const name = self.$("orderName").value.trim();
              const addr = self.$("orderAddress").value.trim();
              if(!name || !addr) { alert("Fill Name and Address"); return; }
              let msg = `ðŸ£ ORDER\nðŸ‘¤ Name: ${name}\nðŸ“ Addr: ${addr}\n------------------\n`;
              self.state.cart.forEach(item => {
                  let p = self.catalog.find(x => x.id === item.id);
                  msg += `â€¢ ${item.qty}x ${p.title} (${p.price * item.qty} DH)\n`;
              });
              window.open(`https://wa.me/${self.config.waPhone}?text=${encodeURIComponent(msg)}`);
          }
        }, true);
      },
      init: function() { this.renderMenu(); this.renderCart(); this.renderWish(); this.bindEvents(); }
    };
    FE.init();
  })();
});
//
