body.script:
//
document.addEventListener("DOMContentLoaded", function() {
  (function(){
    const FE = {
      config: { waPhone: "212600000000", storeName: "EVENT SUSHI", hours: { open: 1, close: 23 } },
      catalog: [
        { id: "p1", title: "Salmon Lovers Box", price: 120, cat: "Special Rolls", imgs: ["https://images.unsplash.com/photo-1579871494447-9811cf80d66c?w=600", "https://images.unsplash.com/photo-1553621042-f6e147245754?w=600"], desc: "12 pieces of premium fresh salmon selection." },
        { id: "p2", title: "Dragon Platter", price: 155, cat: "Special Rolls", imgs: ["https://images.unsplash.com/photo-1553621042-f6e147245754?w=600"], desc: "Signature roll topped with grilled unagi." },
        { id: "p3", title: "Tuna Tataki", price: 90, cat: "Nigiri", imgs: ["https://images.unsplash.com/photo-1617196034796-73dfa7b1fd56?q=80&w=600"], desc: "Seared tuna with garlic sauce." },
        { id: "p4", title: "Golden Maki", price: 110, cat: "Special Rolls", imgs: ["https://images.unsplash.com/photo-1611143669185-af224c5e3252?q=80&w=600"], desc: "Crispy shrimp maki with golden topping." },
        { id: "p5", title: "California Roll", price: 85, cat: "Special Rolls", imgs: ["https://images.unsplash.com/photo-1579584425555-c3ce17fd4351?q=80&w=600"], desc: "Crab stick, avocado, cucumber." },
        { id: "p6", title: "Miso Soup", price: 25, cat: "Sides", imgs: ["https://images.unsplash.com/photo-1547592166-23ac45744acd?q=80&w=600"], desc: "Traditional soybean paste soup." }
      ],
      state: { 
          filter: "all", 
          sort: "default", 
          cart: JSON.parse(localStorage.getItem("fe_cart") || "[]"), 
          wish: JSON.parse(localStorage.getItem("fe_wish") || "[]"), 
          qvId: null, 
          qvQty: 1, 
          qvImgIdx: 0, 
          isOpen: true, 
          searchTerm: "",
          promo: null // Stores active promo { code, percent }
      },
      $: id => document.getElementById(id),
      $$: s => document.querySelectorAll(s),

      /* MODULE: TOAST NOTIFICATION ENGINE */
      toast: function(message, type = 'info') {
          const container = this.$('feToastContainer');
          if (!container) return;

          const el = document.createElement('div');
          el.className = `fe-toast toast-${type}`;

          let iconClass = 'bi-info-circle-fill';
          if (type === 'success') iconClass = 'bi-check-circle-fill';
          if (type === 'warning') iconClass = 'bi-exclamation-triangle-fill';
          if (type === 'error') iconClass = 'bi-x-circle-fill';

          el.innerHTML = `
              <i class="bi ${iconClass} fs-4"></i>
              <div class="fe-toast-content">${message}</div>
          `;

          container.appendChild(el);

          setTimeout(() => {
              el.style.animation = 'fadeOutRight 0.4s forwards';
              setTimeout(() => {
                  if(el.parentElement) el.remove();
              }, 400);
          }, 3500);
      },

      /* MODULE: STORE STATUS LOGIC */
      checkStoreStatus: function() {
          const now = new Date();
          const hour = now.getHours();
          const day = now.getDay(); 
          const { open, close } = this.config.hours;
          let isOpenNow = (hour >= open && hour < close);
          this.state.isOpen = isOpenNow;

          const widget = this.$("feStatusWidget");
          const statusText = widget.querySelector(".fe-status-text");
          const banner = this.$("feStatusBanner");
          const orderBtn = this.$("btnOrderWA");

          if (isOpenNow) {
              widget.classList.remove("status-closed"); widget.classList.add("status-open");
              statusText.textContent = "OPEN NOW";
              banner.style.display = "none";
              document.body.classList.remove("is-closed");
              if(orderBtn) { orderBtn.classList.remove("btn-closed"); orderBtn.textContent = "COMPLETE ORDER"; orderBtn.disabled = false; }
          } else {
              widget.classList.remove("status-open"); widget.classList.add("status-closed");
              statusText.textContent = "CLOSED";
              banner.style.display = "flex";
              document.body.classList.add("is-closed");
              if(orderBtn) { orderBtn.classList.add("btn-closed"); orderBtn.textContent = "SHOP CLOSED"; orderBtn.disabled = true; }
          }
      },

      save: function() { localStorage.setItem("fe_cart",JSON.stringify(this.state.cart)); localStorage.setItem("fe_wish",JSON.stringify(this.state.wish)); },

      relocateUI: function() {
          const ui = ['sideWishPanel', 'sideCartPanel', 'feSideBackdrop', 'modalQuickView', 'feStatusBanner', 'feToastContainer'];
          ui.forEach(id => { const el = this.$(id); if(el && el.parentElement !== document.body) { document.body.appendChild(el); } });
      },

      openSidebar: function(id) { 
        const sidebar = this.$(id); const backdrop = this.$("feSideBackdrop");
        sidebar.classList.add("open"); sidebar.style.visibility = "visible"; sidebar.style.right = "0";
        backdrop.style.display = "block"; setTimeout(() => backdrop.style.opacity = "1", 10); 
      },
      closeAll: function() { 
        this.$$(".fe-sidebar").forEach(s => { s.classList.remove("open"); s.style.right = "-110%"; });
        this.$$(".fe-modal-backdrop").forEach(m => m.style.display = "none"); 
        const b = this.$("feSideBackdrop"); b.style.opacity = "0"; setTimeout(() => b.style.display = "none", 300); 
      },

      /* MODULE: PROMO ENGINE (Step 19) */
      applyPromo: function(code) {
          const upperCode = code.toUpperCase().trim();

          // Define Promo Rules
          if (upperCode === "SUSHI10") {
              this.state.promo = { code: upperCode, percent: 10 };
              this.toast("Code Applied: 10% Off", "success");
          } else if (upperCode === "WELCOME5") {
              this.state.promo = { code: upperCode, percent: 5 };
              this.toast("Code Applied: 5% Off", "success");
          } else {
              this.toast("Invalid Promo Code", "error");
          }
          this.renderCart();
      },

      removePromo: function() {
          this.state.promo = null;
          this.$("fePromoInput").value = "";
          this.toast("Promo Removed", "info");
          this.renderCart();
      },

      /* MODULE: RENDER MENU (WITH SEARCH, FILTER, AND SORT) */
      renderMenu: function() {
        let items = this.catalog;

        // 1. SEARCH FILTER
        if (this.state.searchTerm && this.state.searchTerm.length > 0) {
           const term = this.state.searchTerm.toLowerCase();
           items = items.filter(p => 
              p.title.toLowerCase().includes(term) || 
              (p.desc && p.desc.toLowerCase().includes(term))
           );
        } else {
           // 2. CATEGORY FILTER (If not searching)
           if (this.state.filter !== "all") {
              items = items.filter(p => p.cat === this.state.filter);
           }
        }

        // 3. SORT ENGINE
        if (this.state.sort === "price-asc") {
            items.sort((a, b) => a.price - b.price);
        } else if (this.state.sort === "price-desc") {
            items.sort((a, b) => b.price - a.price);
        }

        // RENDER
        if (items.length === 0) {
           this.$("feMenuGrid").innerHTML = `
             <div class="fe-no-results">
               <i class="bi bi-emoji-frown"></i>
               <h5>No sushi found.</h5>
               <p>Try clearing your search or selecting a different category.</p>
             </div>`;
           return;
        }

        this.$("feMenuGrid").innerHTML = items.map(p => `
          <div class="fe-card reveal js-card" data-id="${p.id}">
            <div class="card-img-box"><img src="${p.imgs[0]}" loading="lazy"/><div class="card-actions">
                <button class="grid-btn grid-btn-cart js-quick-add ${!this.state.isOpen?'btn-closed':''}" data-id="${p.id}">${this.state.isOpen?'Add':'Closed'}</button>
                <button class="grid-btn grid-btn-wish js-wish-toggle ${this.state.wish.includes(p.id)?'active':''}" data-id="${p.id}"><i class="bi bi-heart-fill">&#160;</i></button>
            </div></div>
            <div class="card-body p-3"><h6 class="fw-bold m-0">${p.title}</h6><div class="mt-2"><b>${p.price} DH</b></div></div>
          </div>`).join('');
      },

      renderCart: function() {
        const valid = this.state.cart.filter(i=>this.catalog.find(p=>p.id===i.id));

        // Calculate Totals
        const subtotal = valid.reduce((s,i)=>s+(this.catalog.find(x=>x.id===i.id).price*i.qty),0);
        let discount = 0;
        if(this.state.promo) {
            discount = subtotal * (this.state.promo.percent / 100);
        }
        const total = Math.max(0, subtotal - discount);

        // Render List
        this.$("feCartList").innerHTML = valid.map(i => {
          let p=this.catalog.find(x=>x.id===i.id);
          return `<div class="row-item"><img src="${p.imgs[0]}"/><div><h6 class="m-0 fw-bold">${p.title}</h6><small>${p.price} DH x ${i.qty}</small></div><div class="ms-auto js-del-cart fe-clickable" data-id="${i.id}" style="color:red; font-weight:900">DEL</div></div>`;
        }).join('');

        // Render Totals
        this.$("feFinalTotalDisplay").textContent = Math.round(total) + " DH";

        const discRow = this.$("feDiscountRow");
        if(this.state.promo) {
            discRow.style.display = "flex";
            this.$("feDiscountVal").textContent = `-${Math.round(discount)} DH (-${this.state.promo.percent}%)`;
        } else {
            discRow.style.display = "none";
        }

        this.$$(".js-cart-meta").forEach(el=>el.style.display = valid.length ? "flex" : "none");
        this.$("cartBadge").textContent = valid.reduce((s,i)=>s+i.qty,0);
        this.$("cartBadge").style.display = valid.length ? "flex" : "none";
        this.save();
      },

      renderWish: function() {
        const valid = this.state.wish.filter(id=>this.catalog.find(p=>p.id===id));
        this.$("feWishList").innerHTML = valid.map(id => {
          let p=this.catalog.find(x=>x.id===id);
          return `<div class="row-item"><img src="${p.imgs[0]}"/><div><h6 class="m-0 fw-bold">${p.title}</h6><button class="btn btn-sm btn-dark js-quick-add fe-clickable" data-id="${p.id}">+ Basket</button></div><div class="ms-auto js-del-wish fe-clickable" data-id="${id}" style="font-weight:900">X</div></div>`;
        }).join('');
        this.$("wishBadge").textContent = valid.length;
        this.$("wishBadge").style.display = valid.length ? "flex" : "none";
        this.save();
      },

      updateQVImage: function(idx) {
          const p = this.catalog.find(x => x.id === this.state.qvId);
          if(!p || !p.imgs[idx]) return;
          this.state.qvImgIdx = idx; this.$("feQVImg").src = p.imgs[idx];
          this.$$(".qv-thumb").forEach((th, i) => th.classList.toggle("active", i === idx));
      },

      bindEvents: function() {
        const self = this;
        document.addEventListener("click", e => {
          const t = e.target;

          // 1. SIDEBAR TOGGLES
          if (t.closest("#cartToggle")) { self.openSidebar("sideCartPanel"); return; }
          if (t.closest("#wishToggle")) { self.openSidebar("sideWishPanel"); return; }
          if (t.closest(".js-close-all") || t.id === "feSideBackdrop" || t.id === "modalQuickView") { self.closeAll(); return; }

          // 2. CATEGORY FILTER
          if (t.closest(".cat-item")) {
              const searchInputDesktop = self.$("feSearchInput");
              const searchInputMobile = self.$("feSearchInputMobile");
              if(searchInputDesktop) searchInputDesktop.value = "";
              if(searchInputMobile) searchInputMobile.value = "";
              self.state.searchTerm = "";

              self.$$(".cat-item").forEach(c => c.classList.remove("active"));
              t.closest(".cat-item").classList.add("active");
              self.state.filter = t.closest(".cat-item").dataset.cat;
              self.renderMenu(); return;
          }

          // 3. PROMO APPLY (Step 19)
          if (t.id === "feApplyPromo") {
              const code = self.$("fePromoInput").value;
              if(code) self.applyPromo(code);
              return;
          }
          // 4. REMOVE PROMO
          if (t.closest(".js-remove-promo")) {
              self.removePromo();
              return;
          }

          // 5. WISHLIST TOGGLE
          if (t.closest(".js-wish-toggle")) {
              e.stopPropagation(); 
              const btn = t.closest(".js-wish-toggle");
              let id = btn.dataset.id;
              if(!id && self.state.qvId) id = self.state.qvId;

              if(id) {
                  const index = self.state.wish.indexOf(id);
                  if (index === -1) { 
                      self.state.wish.push(id); 
                      self.toast("Added to favorites", "success");
                  } else { 
                      self.state.wish.splice(index, 1); 
                      self.toast("Removed from favorites", "info");
                  }
                  self.save(); self.renderWish(); self.renderMenu();

                  const qvBtn = self.$("btnModalWishlist");
                  if(qvBtn && self.state.qvId === id) {
                      if(self.state.wish.includes(id)) { qvBtn.style.color = "var(--fe-danger)"; qvBtn.style.borderColor = "var(--fe-danger)"; }
                      else { qvBtn.style.color = ""; qvBtn.style.borderColor = ""; }
                  }
              }
              return;
          }

          // 6. NAVIGATION & THUMBS
          if (t.closest(".js-qv-nav")) {
              const p = self.catalog.find(x => x.id === self.state.qvId);
              let nextIdx = self.state.qvImgIdx + parseInt(t.closest(".js-qv-nav").dataset.dir);
              if(nextIdx < 0) nextIdx = p.imgs.length - 1; if(nextIdx >= p.imgs.length) nextIdx = 0;
              self.updateQVImage(nextIdx); return;
          }
          if (t.closest(".qv-thumb")) {
              const idx = Array.from(self.$$(".qv-thumb")).indexOf(t.closest(".qv-thumb"));
              self.updateQVImage(idx); return;
          }

          // 7. QUICKVIEW TRIGGER
          const card = t.closest(".js-card");
          if (card && !t.closest(".card-actions")) {
            const p = self.catalog.find(x=>x.id===card.dataset.id);
            self.state.qvId=p.id; self.state.qvQty=1; self.state.qvImgIdx=0;
            self.$("feQVImg").src=p.imgs[0]; self.$("feQVTitle").textContent=p.title; self.$("feQVPrice").textContent=p.price + " DH"; self.$("feQVDesc").textContent=p.desc||""; self.$("feQVQtyVal").textContent="1";
            self.$("feQVThumbGrid").innerHTML = p.imgs.length > 1 ? p.imgs.map((src, i) => `<img class="qv-thumb" src="${src}" style="width:60px; height:60px; border-radius:8px; cursor:pointer; object-fit:cover; border:1px solid ${i===0?'#000':'#ddd'}"/>`).join('') : '';

            const qvBtn = self.$("btnModalWishlist");
            if(self.state.wish.includes(p.id)){ qvBtn.style.color = "var(--fe-danger)"; qvBtn.style.borderColor = "var(--fe-danger)"; }
            else { qvBtn.style.color = ""; qvBtn.style.borderColor = ""; }

            const btnAdd = self.$("btnModalAddToCart");
            btnAdd.classList.toggle("btn-closed", !self.state.isOpen);
            if(!self.state.isOpen) btnAdd.textContent = "Shop Closed";
            else btnAdd.textContent = "Add to Basket";

            self.$("modalQuickView").style.display="flex"; return;
          }

          // 8. QTY MODIFIER
          if (t.classList.contains("js-modal-qty")) {
            t.dataset.op === "+" ? self.state.qvQty++ : (self.state.qvQty>1 ? self.state.qvQty-- : null);
            self.$("feQVQtyVal").textContent = self.state.qvQty; return;
          }

          // 9. ADD TO CART
          if (t.id==="btnModalAddToCart" || t.closest(".js-quick-add")) {
            if(!self.state.isOpen) { 
                self.toast("We are currently closed!", "warning"); 
                return; 
            }
            const id = t.closest(".js-quick-add") ? t.closest(".js-quick-add").dataset.id : self.state.qvId;
            const qty = t.id==="btnModalAddToCart" ? self.state.qvQty : 1;
            const ex=self.state.cart.find(x=>x.id===id);
            ex ? ex.qty+=qty : self.state.cart.push({id, qty});

            self.toast("Added to basket", "success");
            self.closeAll(); self.renderCart(); self.openSidebar("sideCartPanel"); return;
          }

          // 10. DELETE ACTIONS
          if (t.closest(".js-del-cart")) { 
              self.state.cart = self.state.cart.filter(x => x.id !== t.closest(".js-del-cart").dataset.id); 
              self.toast("Item removed from basket", "info");
              self.renderCart(); return; 
          }
          if (t.closest(".js-del-wish")) { 
              self.state.wish = self.state.wish.filter(x => x !== t.closest(".js-del-wish").dataset.id); 
              self.toast("Removed from favorites", "info");
              self.renderWish(); self.renderMenu(); return; 
          }

          // 11. WHATSAPP ORDER (Step 19)
          if (t.id === "btnOrderWA") {
              const name = self.$("orderName").value.trim();
              const address = self.$("orderAddress").value.trim();

              if(!name || !address) {
                  self.toast("Please fill in Name and Address", "warning");
                  return;
              }

              const valid = self.state.cart.filter(i=>self.catalog.find(p=>p.id===i.id));
              if(valid.length === 0) return;

              let msg = `*NEW ORDER from ${self.config.storeName}*%0A%0A`;
              msg += `*Customer:* ${name}%0A`;
              msg += `*Address:* ${address}%0A`;
              msg += `------------------%0A`;

              let subTotal = 0;
              valid.forEach(i => {
                  const p = self.catalog.find(x=>x.id===i.id);
                  const lineTotal = p.price * i.qty;
                  subTotal += lineTotal;
                  msg += `â€¢ ${p.title} (x${i.qty}) - ${lineTotal} DH%0A`;
              });

              // Apply Promo to Message
              if(self.state.promo) {
                  const disc = subTotal * (self.state.promo.percent / 100);
                  msg += `------------------%0A`;
                  msg += `*Promo Code:* ${self.state.promo.code} (-${self.state.promo.percent}%)%0A`;
                  msg += `*Discount:* -${Math.round(disc)} DH%0A`;
                  subTotal = subTotal - disc;
              }

              msg += `------------------%0A`;
              msg += `*TOTAL TO PAY: ${Math.round(subTotal)} DH*`;

              const waUrl = `https://wa.me/${self.config.waPhone}?text=${msg}`;
              window.open(waUrl, '_blank');
              return;
          }
        }, true);

        /* SEARCH EVENT LISTENER (DESKTOP & MOBILE) */
        const handleSearch = (e) => {
            const val = e.target.value;
            this.state.searchTerm = val;
            this.renderMenu();
        };
        const s1 = this.$("feSearchInput");
        const s2 = this.$("feSearchInputMobile");
        if(s1) s1.addEventListener("input", handleSearch);
        if(s2) s2.addEventListener("input", handleSearch);

        /* SORT EVENT LISTENER */
        const handleSort = (e) => {
            const val = e.target.value;
            const text = e.target.options[e.target.selectedIndex].text;
            this.state.sort = val;
            this.renderMenu();
            this.toast(`Sorted by: ${text}`, "info");
        };
        const sortSelect = this.$("feSortSelect");
        if(sortSelect) sortSelect.addEventListener("change", handleSort);
      },
      init: function() { this.relocateUI(); this.checkStoreStatus(); this.renderMenu(); this.renderCart(); this.renderWish(); this.bindEvents(); }
    };
    FE.init();
  })();
});
//
