          }
      },
      state: { 
          filter: "all", 
          sort: "default", 
          cart: JSON.parse(localStorage.getItem("fe_cart") || "[]"), 
          wish: JSON.parse(localStorage.getItem("fe_wish") || "[]"), 
          qvId: null, 
          qvQty: 1, 
          qvImgIdx: 0, 
          isOpen: true, 
          searchTerm: "",
          promo: null,
          theme: localStorage.getItem("fe_theme") || "system",
          lang: localStorage.getItem("fe_lang") || "en"
      },
      $: id => document.getElementById(id),
      $$: s => document.querySelectorAll(s),

      // Helper to get translated text
      t: function(key) { return this.dictionary[this.state.lang][key] || key; },

      /* MODULE: TOAST NOTIFICATION ENGINE */
      toast: function(message, type = 'info') {
          const container = this.$('feToastContainer');
          if (!container) return;

          const el = document.createElement('div');
          el.className = `fe-toast toast-${type}`;

          let iconClass = 'bi-info-circle-fill';
          if (type === 'success') iconClass = 'bi-check-circle-fill';
          if (type === 'warning') iconClass = 'bi-exclamation-triangle-fill';
          if (type === 'error') iconClass = 'bi-x-circle-fill';

          el.innerHTML = `
              <i class="bi ${iconClass} fs-4"></i>
              <div class="fe-toast-content">${message}</div>
          `;

          container.appendChild(el);

          setTimeout(() => {
              el.style.animation = 'fadeOutRight 0.4s forwards';
              setTimeout(() => {
                  if(el.parentElement) el.remove();
              }, 400);
          }, 3500);
      },

      /* MODULE: STORE STATUS LOGIC */
      checkStoreStatus: function() {
          const now = new Date();
          const hour = now.getHours();
          const day = now.getDay(); 
          const { open, close } = this.config.hours;
          let isOpenNow = (hour >= open && hour < close);
          this.state.isOpen = isOpenNow;

          const widget = this.$("feStatusWidget");
          const statusText = widget.querySelector(".fe-status-text");
          const banner = this.$("feStatusBanner");
          const orderBtn = this.$("btnOrderWA");

          if (isOpenNow) {
              widget.classList.remove("status-closed"); widget.classList.add("status-open");
              statusText.textContent = this.t("open");
              banner.style.display = "none";
              document.body.classList.remove("is-closed");
              if(orderBtn) { orderBtn.classList.remove("btn-closed"); orderBtn.textContent = this.t("complete"); orderBtn.disabled = false; }
          } else {
              widget.classList.remove("status-open"); widget.classList.add("status-closed");
              statusText.textContent = this.t("closedBtn");
              banner.style.display = "flex";
              document.body.classList.add("is-closed");
              if(orderBtn) { orderBtn.classList.add("btn-closed"); orderBtn.textContent = this.t("closedBtn"); orderBtn.disabled = true; }
          }
      },

      /* MODULE: THEME ENGINE */
      initTheme: function() {
          const saved = this.state.theme;
          const prefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;

          if (saved === 'dark' || (saved === 'system' && prefersDark)) {
              document.body.classList.add('dark-mode');
              this.$('feThemeToggle').innerHTML = '<i class="bi bi-sun-fill"></i>';
          } else {
              document.body.classList.remove('dark-mode');
              this.$('feThemeToggle').innerHTML = '<i class="bi bi-moon-stars-fill"></i>';
          }
      },
      toggleTheme: function() {
          if (document.body.classList.contains('dark-mode')) {
              document.body.classList.remove('dark-mode');
              this.state.theme = 'system';
              this.$('feThemeToggle').innerHTML = '<i class="bi bi-moon-stars-fill"></i>';
          } else {
              document.body.classList.add('dark-mode');
              this.state.theme = 'dark';
              this.$('feThemeToggle').innerHTML = '<i class="bi bi-sun-fill"></i>';
          }
          localStorage.setItem("fe_theme", this.state.theme);
      },

      /* MODULE: SHARE PRODUCT */
      shareProduct: function(p) {
          const link = window.location.href.split('#')[0] + `?id=${p.id}`;
          navigator.clipboard.writeText(link).then(() => {
              this.toast("Link copied!", "success");
          }, () => {
              const shareMsg = `${this.t("share")}: ${p.title} from EVENT SUSHI - ${link}`;
              const waUrl = `https://wa.me/?text=${shareMsg}`;
              window.open(waUrl, '_blank');
          });
      },

      /* MODULE: GENERATE ORDER ID */
      generateOrderId: function() {
          return "#ORD-" + Math.floor(1000 + Math.random() * 9000);
      },

      save: function() { 
          localStorage.setItem("fe_cart",JSON.stringify(this.state.cart)); 
          localStorage.setItem("fe_wish",JSON.stringify(this.state.wish)); 
          localStorage.setItem("fe_theme", this.state.theme);
          localStorage.setItem("fe_lang", this.state.lang);
      },

      relocateUI: function() {
          const ui = ['sideWishPanel', 'sideCartPanel', 'feSideBackdrop', 'modalQuickView', 'feStatusBanner', 'feToastContainer', 'feReceiptModal'];
          ui.forEach(id => { const el = this.$(id); if(el && el.parentElement !== document.body) { document.body.appendChild(el); } });
      },

      openSidebar: function(id) { 
        const sidebar = this.$(id); const backdrop = this.$("feSideBackdrop");
        sidebar.classList.add("open"); sidebar.style.visibility = "visible"; sidebar.style.right = "0";
        backdrop.style.display = "block"; setTimeout(() => backdrop.style.opacity = "1", 10); 
      },
      closeAll: function() { 
        this.$$(".fe-sidebar").forEach(s => { s.classList.remove("open"); s.style.right = "-110%"; });
        this.$$(".fe-modal-backdrop").forEach(m => m.style.display = "none"); 
        const b = this.$("feSideBackdrop"); b.style.opacity = "0"; setTimeout(() => b.style.display = "none", 300); 
      },

      /* MODULE: PROMO ENGINE */
      applyPromo: function(code) {
          const upperCode = code.toUpperCase().trim();
          if (upperCode === "SUSHI10") {
              this.state.promo = { code: upperCode, percent: 10 };
              this.toast("10% Off Applied", "success");
          } else if (upperCode === "WELCOME5") {
              this.state.promo = { code: upperCode, percent: 5 };
              this.toast("5% Off Applied", "success");
          } else {
              this.toast("Invalid Promo Code", "error");
          }
          this.renderCart();
      },

      removePromo: function() {
          this.state.promo = null;
          this.$("fePromoInput").value = "";
          this.toast("Promo Removed", "info");
          this.renderCart();
      },

      /* MODULE: RENDER MENU */
      renderMenu: function() {
        let items = this.catalog;

        if (this.state.searchTerm && this.state.searchTerm.length > 0) {
           const term = this.state.searchTerm.toLowerCase();
           items = items.filter(p => 
              p.title.toLowerCase().includes(term) || 
              (p.desc && p.desc.toLowerCase().includes(term))
           );
        } else {
           if (this.state.filter !== "all") {
              items = items.filter(p => p.cat === this.state.filter);
           }
        }

        if (this.state.sort === "price-asc") items.sort((a, b) => a.price - b.price);
        else if (this.state.sort === "price-desc") items.sort((a, b) => b.price - a.price);

        if (items.length === 0) {
           this.$("feMenuGrid").innerHTML = `
             <div class="fe-no-results">
               <i class="bi bi-emoji-frown"></i>
               <h5>${this.t("catAll")} Found</h5>
               <p>Try clearing your search or selecting a different category.</p>
             </div>`;
           return;
        }

        this.$("feMenuGrid").innerHTML = items.map(p => {
          // Diet Badges
          let dietBadges = '';
          if(p.diet) {
              const icons = { spicy: 'üå∂Ô∏è', vegan: 'üå±', gf: 'üåæ' };
              dietBadges = `<div class="diet-badges">${p.diet.map(d => `<span class="diet-badge" title="${d}">${icons[d]}</span>`).join('')}</div>`;
          }
          return `
          <div class="fe-card reveal js-card" data-id="${p.id}">
            <div class="card-img-box"><img src="${p.imgs[0]}" loading="lazy"/><div class="card-actions">
                <button class="grid-btn grid-btn-cart js-quick-add ${!this.state.isOpen?'btn-closed':''}" data-id="${p.id}">${this.state.isOpen?this.t("add"):this.t("closedCard")}</button>
                <button class="grid-btn grid-btn-wish js-wish-toggle ${this.state.wish.includes(p.id)?'active':''}" data-id="${p.id}"><i class="bi bi-heart-fill">&#160;</i></button>
            </div></div>
            <div class="card-body p-3"><h6 class="fw-bold m-0">${p.title}</h6><div class="mt-2"><b>${p.price} DH</b></div></div>
            ${dietBadges}
          </div>`;
        }).join('');
      },

      renderCart: function() {
        const valid = this.state.cart.filter(i=>this.catalog.find(p=>p.id===i.id));

        // Calculations
        const subtotal = valid.reduce((s,i)=>s+(this.catalog.find(x=>x.id===i.id).price*i.qty),0);
        let discount = 0;
        if(this.state.promo) discount = subtotal * (this.state.promo.percent / 100);

        let deliveryFee = 0;
        if(valid.length > 0) {
           deliveryFee = (subtotal - discount) >= this.config.delivery.minForFree ? 0 : this.config.delivery.fee;
        }

        const total = Math.max(0, subtotal - discount + deliveryFee);
        const meetsMin = total >= this.config.minOrder;

        // Render List
        this.$("feCartList").innerHTML = valid.map(i => {
          let p=this.catalog.find(x=>x.id===i.id);
          return `<div class="row-item"><img src="${p.imgs[0]}"/><div><h6 class="m-0 fw-bold">${p.title}</h6><small>${p.price} DH x ${i.qty}</small></div><div class="ms-auto js-del-cart fe-clickable" data-id="${i.id}" style="color:red; font-weight:900">DEL</div></div>`;
        }).join('');

        // Render Totals & Delivery
        this.$("feFinalTotalDisplay").textContent = Math.round(total) + " DH";
        this.$("feDeliveryRow").style.display = valid.length > 0 ? "flex" : "none";
        this.$("feDeliveryFee").textContent = deliveryFee === 0 ? this.t("delivery") : deliveryFee + " DH";

        // Min Order (Step 25)
        const minWarning = this.$("feMinOrderWarning");
        const orderBtn = this.$("btnOrderWA");

        if(valid.length > 0 && !meetsMin) {
            minWarning.style.display = "block";
            minWarning.textContent = `${this.t("minWarning")} (${Math.round(this.config.minOrder - total)} DH ${this.t("minMsg")})`;
            orderBtn.classList.add("btn-min-order-disabled");
            orderBtn.disabled = true;
            orderBtn.textContent = this.t("minWarning");
        } else {
            minWarning.style.display = "none";
            if(valid.length > 0) {
                orderBtn.classList.remove("btn-min-order-disabled");
                orderBtn.disabled = !this.state.isOpen;
                orderBtn.textContent = this.t("complete");
            } else {
                orderBtn.disabled = true;
                orderBtn.textContent = this.t("closedBtn");
            }
        }

        const discRow = this.$("feDiscountRow");
        if(this.state.promo) {
            discRow.style.display = "flex";
            this.$("feDiscountVal").textContent = `-${Math.round(discount)} DH (-${this.state.promo.percent}%)`;
        } else {
            discRow.style.display = "none";
        }

        this.$$(".js-cart-meta").forEach(el=>el.style.display = valid.length ? "flex" : "none");
        this.$("cartBadge").textContent = valid.reduce((s,i)=>s+i.qty,0);
        this.$("cartBadge").style.display = valid.length ? "flex" : "none";
        this.save();
      },

      renderWish: function() {
        const valid = this.state.wish.filter(id=>this.catalog.find(p=>p.id===id));
        this.$("feWishList").innerHTML = valid.map(id => {
          let p=this.catalog.find(x=>x.id===id);
          return `<div class="row-item"><img src="${p.imgs[0]}"/><div><h6 class="m-0 fw-bold">${p.title}</h6><button class="btn btn-sm btn-dark js-quick-add fe-clickable" data-id="${p.id}">+ Basket</button></div><div class="ms-auto js-del-wish fe-clickable" data-id="${id}" style="font-weight:900">X</div></div>`;
        }).join('');
        this.$("wishBadge").textContent = valid.length;
        this.$("wishBadge").style.display = valid.length ? "flex" : "none";
        this.save();
      },

      updateQVImage: function(idx) {
          const p = this.catalog.find(x => x.id === this.state.qvId);
          if(!p || !p.imgs[idx]) return;
          this.state.qvImgIdx = idx; this.$("feQVImg").src = p.imgs[idx];
          this.$$(".qv-thumb").forEach((th, i) => th.classList.toggle("active", i === idx));
      },

      openQuickView: function(id) {
          const p = this.catalog.find(x=>x.id===id);
          this.state.qvId = id;
          this.state.qvQty = 1;
          this.state.qvImgIdx = 0;

          // Info
          this.$("feQVImg").src = p.imgs[0];
          this.$("feQVTitle").textContent = p.title;
          this.$("feQVPrice").textContent = p.price + " DH";
          this.$("feQVDesc").textContent = p.desc||"";
          this.$("feQVQtyVal").textContent = "1";
          this.$("feQVThumbGrid").innerHTML = p.imgs.length > 1 ? p.imgs.map((src, i) => `<img class="qv-thumb" src="${src}" style="width:60px; height:60px; border-radius:8px; cursor:pointer; object-fit:cover; border:1px solid ${i===0?'#000':'#ddd'}"/>`).join('') : '';

          // Wishlist Btn
          const qvBtn = this.$("btnModalWishlist");
          if(this.state.wish.includes(p.id)){ qvBtn.style.color = "var(--fe-danger)"; qvBtn.style.borderColor = "var(--fe-danger)"; }
          else { qvBtn.style.color = ""; qvBtn.style.borderColor = ""; }

          const btnAdd = this.$("btnModalAddToCart");
          btnAdd.classList.toggle("btn-closed", !this.state.isOpen);
          if(!this.state.isOpen) btnAdd.textContent = this.t("closedCard");
          else btnAdd.textContent = this.t("add");

          // Ratings
          const stars = this.t("ratingStars").repeat(Math.round(p.rating)) + this.t("ratingEmpty").repeat(5 - Math.round(p.rating));
          this.$("feQVRating").innerHTML = stars;

          if(p.reviews && p.reviews.length > 0) {
              this.$("feQVReviews").style.display = "block";
              this.$("feQVReviewsList").innerHTML = p.reviews.map(r => `
                  <div class="review-item">
                      <div class="reviewer"><span>${r.u}</span> <span class="review-date">${r.d}</span></div>
                      <div class="review-text">"${r.t}"</div>
                  </div>
              `).join('');
          } else {
              this.$("feQVReviews").style.display = "none";
          }

          // Related
          const related = this.catalog.filter(x => x.cat === p.cat && x.id !== p.id).slice(0, 4);
          if(related.length > 0) {
              this.$("feQVRelated").style.display = "block";
              this.$("feQVRelatedList").innerHTML = related.map(r => `
                  <div class="qv-related-item js-qv-related" data-id="${r.id}">
                      <img src="${r.imgs[0]}" alt="${r.title}"/>
                  </div>
              `).join('');
          } else {
              this.$("feQVRelated").style.display = "none";
          }

          this.$("modalQuickView").style.display="flex";
      },

      bindEvents: function() {
        const self = this;
        document.addEventListener("click", e => {
          const t = e.target;

          // THEME TOGGLE
          if (t.closest("#feThemeToggle")) { self.toggleTheme(); return; }

          // SIDEBAR TOGGLES
          if (t.closest("#cartToggle")) { self.openSidebar("sideCartPanel"); return; }
          if (t.closest("#wishToggle")) { self.openSidebar("sideWishPanel"); return; }
          if (t.closest(".js-close-all") || t.id === "feSideBackdrop" || t.id === "modalQuickView") { self.closeAll(); return; }

          // CATEGORY FILTER
          if (t.closest(".cat-item")) {
              const searchInputDesktop = self.$("feSearchInput");
              const searchInputMobile = self.$("feSearchInputMobile");
              if(searchInputDesktop) searchInputDesktop.value = "";
              if(searchInputMobile) searchInputMobile.value = "";
              self.state.searchTerm = "";

              self.$$(".cat-item").forEach(c => c.classList.remove("active"));
              t.closest(".cat-item").classList.add("active");
              self.state.filter = t.closest(".cat-item").dataset.cat;
              self.renderMenu(); return;
          }

          // PROMO APPLY
          if (t.id === "feApplyPromo") {
              const code = self.$("fePromoInput").value;
              if(code) self.applyPromo(code);
              return;
          }
          // REMOVE PROMO
          if (t.closest(".js-remove-promo")) { self.removePromo(); return; }

          // WISHLIST TOGGLE
          if (t.closest(".js-wish-toggle")) {
              e.stopPropagation(); 
              const btn = t.closest(".js-wish-toggle");
              let id = btn.dataset.id;
              if(!id && self.state.qvId) id = self.state.qvId;

              if(id) {
                  const index = self.state.wish.indexOf(id);
                  if (index === -1) { 
                      self.state.wish.push(id); 
                      self.toast("Added to favorites", "success");
                  } else { 
                      self.state.wish.splice(index, 1); 
                      self.toast("Removed from favorites", "info");
                  }
                  self.save(); self.renderWish(); self.renderMenu();

                  const qvBtn = self.$("btnModalWishlist");
                  if(qvBtn && self.state.qvId === id) {
                      if(self.state.wish.includes(id)) { qvBtn.style.color = "var(--fe-danger)"; qvBtn.style.borderColor = "var(--fe-danger)"; }
                      else { qvBtn.style.color = ""; qvBtn.style.borderColor = ""; }
                  }
              }
              return;
          }

          // NAVIGATION & THUMBS
          if (t.closest(".js-qv-nav")) {
              const p = self.catalog.find(x => x.id === self.state.qvId);
              let nextIdx = self.state.qvImgIdx + parseInt(t.closest(".js-qv-nav").dataset.dir);
              if(nextIdx < 0) nextIdx = p.imgs.length - 1; if(nextIdx >= p.imgs.length) nextIdx = 0;
              self.updateQVImage(nextIdx); return;
          }
          if (t.closest(".qv-thumb")) {
              const idx = Array.from(self.$$(".qv-thumb")).indexOf(t.closest(".qv-thumb"));
              self.updateQVImage(idx); return;
          }

          // RELATED ITEM CLICK
          if (t.closest(".js-qv-related")) {
              const id = t.closest(".js-qv-related").dataset.id;
              self.openQuickView(id);
              return;
          }

          // SHARE PRODUCT
          if (t.closest(".js-share-product")) {
              const p = self.catalog.find(x => x.id === self.state.qvId);
              if(p) self.shareProduct(p);
              return;
          }

          // QUICKVIEW TRIGGER
          const card = t.closest(".js-card");
          if (card && !t.closest(".card-actions")) {
            const p = self.catalog.find(x=>x.id===card.dataset.id);
            self.openQuickView(p.id);
            return;
          }

          // QTY MODIFIER
          if (t.classList.contains("js-modal-qty")) {
            t.dataset.op === "+" ? self.state.qvQty++ : (self.state.qvQty>1 ? self.state.qvQty-- : null);
            self.$("feQVQtyVal").textContent = self.state.qvQty; return;
          }

          // ADD TO CART
          if (t.id==="btnModalAddToCart" || t.closest(".js-quick-add")) {
            if(!self.state.isOpen) { 
                self.toast("We are currently closed!", "warning"); 
                return; 
            }
            const id = t.closest(".js-quick-add") ? t.closest(".js-quick-add").dataset.id : self.state.qvId;
            const qty = t.id==="btnModalAddToCart" ? self.state.qvQty : 1;
            const ex=self.state.cart.find(x=>x.id===id);
            ex ? ex.qty+=qty : self.state.cart.push({id, qty});

            self.toast("Added to basket", "success");
            self.closeAll(); self.renderCart(); self.openSidebar("sideCartPanel"); return;
          }

          // DELETE ACTIONS
          if (t.closest(".js-del-cart")) { 
              self.state.cart = self.state.cart.filter(x => x.id !== t.closest(".js-del-cart").dataset.id); 
              self.toast("Item removed from basket", "info");
              self.renderCart(); return; 
          }
          if (t.closest(".js-del-wish")) { 
              self.state.wish = self.state.wish.filter(x => x !== t.closest(".js-del-wish").dataset.id); 
              self.toast("Removed from favorites", "info");
              self.renderWish(); self.renderMenu(); return; 
          }

          // WHATSAPP ORDER
          if (t.id === "btnOrderWA") {
              const name = self.$("orderName").value.trim();
              const address = self.$("orderAddress").value.trim();

              if(!name || !address) {
                  self.toast(self.t("name") + " & " + self.t("address") + " required", "warning");
                  return;
              }

              const valid = self.state.cart.filter(i=>self.catalog.find(p=>p.id===i.id));
              if(valid.length === 0) return;

              const orderId = self.generateOrderId();

              let msg = `*NEW ORDER (${orderId})*%0A%0A`;
              msg += `*Customer:* ${name}%0A`;
              msg += `*Address:* ${address}%0A`;
              msg += `------------------%0A`;

              let subTotal = 0;
              valid.forEach(i => {
                  const p = self.catalog.find(x=>x.id===i.id);
                  const lineTotal = p.price * i.qty;
                  subTotal += lineTotal;
                  msg += `‚Ä¢ ${p.title} (x${i.qty}) - ${lineTotal} DH%0A`;
              });

              // Discount
              let discount = 0;
              if(self.state.promo) {
                  discount = subTotal * (self.state.promo.percent / 100);
                  msg += `------------------%0A`;
                  msg += `*Promo Code:* ${self.state.promo.code} (-${self.state.promo.percent}%)%0A`;
                  msg += `*Discount:* -${Math.round(discount)} DH%0A`;
                  subTotal = subTotal - discount;
              }

              // Delivery Fee
              let delivery = 0;
              if((subTotal) >= self.config.delivery.minForFree) { delivery = 0; }
              else { delivery = self.config.delivery.fee; }

              if(delivery > 0) {
                  msg += `------------------%0A`;
                  msg += `*Delivery Fee:* ${delivery} DH%0A`;
              }

              const total = subTotal + delivery;
              msg += `------------------%0A`;
              msg += `*TOTAL TO PAY: ${Math.round(total)} DH*`;

              // Show Receipt Modal
              self.$("receiptOrderId").textContent = orderId;
              self.$("receiptBody").innerHTML = `
                  <div style="margin-bottom:10px"><b>${self.t("name")}:</b> ${name}</div>
                  <div style="margin-bottom:10px"><b>${self.t("address")}:</b> ${address}</div>
                  <div style="margin-top:20px; border-top:1px dashed #ccc; padding-top:10px;">
                      ${msg.replace(/\n/g, '<br>')}
                  </div>
              `;
              self.$("receiptTotal").textContent = Math.round(total) + " DH";
              self.$("feReceiptModal").style.display = "flex";

              // WhatsApp
              const waUrl = `https://wa.me/${self.config.waPhone}?text=${msg}`;
              window.open(waUrl, '_blank');
              return;
          }

          // Close Receipt Modal
          if (t.id === "btnCloseReceipt" || t.closest("#feReceiptModal")) {
               self.$("feReceiptModal").style.display = "none";
               return;
          }
        }, true);

        /* SEARCH EVENT LISTENER */
        const handleSearch = (e) => {
            const val = e.target.value;
            this.state.searchTerm = val;
            this.renderMenu();
        };
        const s1 = this.$("feSearchInput");
        const s2 = this.$("feSearchInputMobile");
        if(s1) s1.addEventListener("input", handleSearch);
        if(s2) s2.addEventListener("input", handleSearch);

        /* SORT EVENT LISTENER */
        const handleSort = (e) => {
            const val = e.target.value;
            const text = e.target.options[e.target.selectedIndex].text;
            this.state.sort = val;
            this.renderMenu();
            this.toast(`Sorted by: ${text}`, "info");
        };
        const sortSelect = this.$("feSortSelect");
        if(sortSelect) sortSelect.addEventListener("change", handleSort);

        /* LANG EVENT LISTENER */
        const handleLang = (e) => {
            const lang = e.target.value;
            this.state.lang = lang;
            // Re-render everything to update text
            this.renderMenu();
            this.renderCart();
            this.renderWish();
            // Update select text itself
            const options = Array.from(e.target.options);
            options.forEach(o => { o.textContent = this.dictionary[lang][o.value.replace('cat-','').replace('sort-','')] || o.text; });
            // Update dynamic text elements
            this.updateTextElements();
            this.save();
        };
        const langSelect = this.$("feLangSelect");
        if(langSelect) langSelect.addEventListener("change", handleLang);
      },
      updateTextElements: function() {
          // Helper to update static HTML elements based on current lang
          const map = [
              { id: "feStatusBanner", key: "closed" },
              { id: "heroTitle", html: true, key: "heroTitle" },
              { id: "heroSub", html: true, key: "heroSub" },
              { id: "hoursTitle", html: true, key: "hoursTitle" },
              { id: "monFri", html: true, key: "monFri" },
              { id: "satSun", html: true, key: "satSun" },
              { id: "catAll", className: ".cat-item[data-cat='all']", html: true, key: "catAll" },
              { id: "catSpecial", className: ".cat-item[data-cat='Special Rolls']", html: true, key: "catRolls" },
              { id: "catNigiri", className: ".cat-item[data-cat='Nigiri']", html: true, key: "catNigiri" },
              { id: "catSides", className: ".cat-item[data-cat='Sides']", html: true, key: "catSides" },
              { id: "feSearchInput", placeholder: true, key: "search" },
              { id: "feSearchInputMobile", placeholder: true, key: "search" },
              { id: "basket", key: "basket" },
              { id: "saved", key: "saved" },
              { id: "nameLabel", key: "name" },
              { id: "addressLabel", key: "address" },
              { id: "apply", key: "apply" },
              { id: "discountLabel", key: "discount" },
              { id: "deliveryLabel", key: "delivery" },
              { id: "totalLabel", key: "total" },
              { id: "complete", key: "complete" },
              { id: "minWarning", key: "minWarning" },
              { id: "closeReceipt", key: "close" },
              { id: "shareTitle", attr: "title", key: "share" }, // Null check needed
              { id: "reviewsTitle", key: "reviewsTitle" },
              { id: "relatedTitle", key: "relatedTitle" },
              { id: "addBtn", key: "add" },
              { id: "totalReceipt", key: "total" }
          ];

          map.forEach(item => {
              if(item.placeholder && this.$(item.id)) this.$(item.id).placeholder = this.t(item.key);
              if(item.html && this.$(item.id)) this.$(item.id).textContent = this.t(item.key);
              if(item.className) this.$$(item.className).forEach(el => el.textContent = this.t(item.key));
              if(item.attr && this.$(item.id)) this.$(item.id).setAttribute(item.attr, this.t(item.key)); // FIXED: Added null check
          });
      },

      init: function() { 
          // 1. Render Skeletons
          const grid = this.$("feMenuGrid");
          grid.innerHTML = Array(6).fill(0).map(() => `
              <div class="fe-card fe-skeleton">
                  <div class="card-img-box fe-skeleton-img"></div>
                  <div class="card-body p-3">
                      <div class="fe-skeleton-text"></div>
                      <div class="mt-2"><b class="fe-skeleton-text" style="width:50%"></b></div>
                  </div>
              </div>
          `).join('');

          // 2. Initialize Real Data after short delay
          setTimeout(() => {
              this.updateTextElements();
              this.checkStoreStatus(); 
              this.renderMenu(); 
              this.renderCart(); 
              this.renderWish(); 
              this.bindEvents();
          }, 800);
      }
    };
    FE.init();
  })();
});
//
